import React, { useEffect, useState } from "react";
import { FaCheck, FaPencilAlt, FaPlus, FaSearch, FaTrash, FaMoon, FaSun } from "react-icons/fa";
import { ToastContainer } from "react-toastify";
import { CreateTask, DeleteTask, GetAllTasks, UpdateTask } from "./api";
import { notify } from "./utils";
import 'bootstrap/dist/css/bootstrap.min.css';
import 'bootstrap/dist/js/bootstrap.bundle.min';
import 'react-toastify/dist/ReactToastify.css';
import './TaskManager.css'; // Custom CSS

function TaskManager() {
    const [input, setInput] = useState('');
    const [dueDate, setDueDate] = useState('');
    const [description, setDescription] = useState('');
    const [tasks, setTasks] = useState([]);
    const [copyTasks, setcopyTasks] = useState([]);
    const [updateTask, setupdateTask] = useState(null);
    const [isDarkMode, setIsDarkMode] = useState(false);

    const handleTask = () => {
        if (updateTask && input && dueDate) {
            const obj = {
                taskName: input,
                isDone: updateTask.isDone,
                dueDate,
                description,
                _id: updateTask._id
            }
            handleUpdate(obj);
        } else if (updateTask === null && input && dueDate) {
            handleAddTask();
        }
        setInput('');
        setDueDate('');
        setDescription('');
    }

    useEffect(() => {
        if (updateTask) {
            setInput(updateTask.taskName);
            setDueDate(updateTask.dueDate.split('T')[0]);
            setDescription(updateTask.description || ''); // Set description for editing
        }
    }, [updateTask]);

    const handleAddTask = async () => {
        const obj = {
            taskName: input,
            isDone: false,
            dueDate,
            description
        }
        try {
            const { success, message } = await CreateTask(obj);
            if (success) {
                notify(message, 'success');
            } else {
                notify(message, 'error');
            }
            fetchAllTasks();
        } catch (err) {
            console.error(err);
            notify('Failed to create task', 'error');
        }
    }

    const fetchAllTasks = async () => {
        try {
            const { data } = await GetAllTasks();
            setTasks(data);
            setcopyTasks(data);
        } catch (err) {
            console.error(err);
            notify('Failed to fetch tasks', 'error');
        }
    }

    const getReminders = (tasks) => {
        const today = new Date();
        const tomorrow = new Date();
        tomorrow.setDate(today.getDate() + 1);

        return tasks.filter(task => {
            const dueDate = new Date(task.dueDate);
            return dueDate.toDateString() === today.toDateString() ||
                   dueDate.toDateString() === tomorrow.toDateString();
        });
    };

    const reminders = getReminders(tasks);


    useEffect(() => {
        fetchAllTasks();
    }, []);

    const handleDeleteTask = async (id) => {
        try {
            const { success, message } = await DeleteTask(id);
            if (success) {
                notify(message, 'success');
            } else {
                notify(message, 'error');
            }
            fetchAllTasks();
        } catch (err) {
            console.error(err);
            notify('Failed to delete task', 'error');
        }
    }

    const handleCheckAndUncheck = async (item) => {
        const { _id, isDone, taskName, dueDate, description } = item;
        const obj = {
            taskName,
            isDone: !isDone,
            dueDate,
            description
        }
        try {
            const { success, message } = await UpdateTask(_id, obj);
            if (success) {
                notify(message, 'success');
            } else {
                notify(message, 'error');
            }
            fetchAllTasks();
        } catch (err) {
            console.error(err);
            notify('Failed to update task', 'error');
        }
    }

    const handleUpdate = async (item) => {
        const { _id, isDone, taskName, dueDate, description } = item;
        const obj = {
            taskName,
            isDone,
            dueDate,
            description
        }
        try {
            const { success, message } = await UpdateTask(_id, obj);
            if (success) {
                notify(message, 'success');
            } else {
                notify(message, 'error');
            }
            fetchAllTasks();
        } catch (err) {
            console.error(err);
            notify('Failed to update task', 'error');
        }
    }

    const handleSearch = (e) => {
        const term = e.target.value.toLowerCase();
        const oldTask = [...copyTasks];
        const results = oldTask.filter((item) => item.taskName.toLowerCase().includes(term));
        setTasks(results);
    }

    const toggleDarkMode = () => {
        setIsDarkMode(!isDarkMode);
        document.documentElement.setAttribute('data-theme', isDarkMode ? 'light' : 'dark');
    }

    return (
        <div className='d-flex flex-column align-items-center w-50 m-auto mt-5'>
            <h1 className="mb-5">Task Management System</h1>
            {/* input and search-box */}
            <div className="d-flex justify-content-between align-item-center mb-4 w-100">
                <div className="input-group flex-grow-1 me-2">
                    <input
                        type="text"
                        value={input}
                        onChange={(e) => setInput(e.target.value)}
                        className="form-control me-1"
                        placeholder="Add a new Task"
                    />
                    <input
                        type="date"
                        value={dueDate}
                        onChange={(e) => setDueDate(e.target.value)}
                        className="form-control me-1"
                    />
                    <button onClick={handleTask} className="btn btn-success btn-sm me-2">
                        <FaPlus className='m-2' />
                    </button>
                </div>

                <div className="input-group flex-grow-1">
                    <span className="input-group-text">
                        <FaSearch />
                    </span>
                    <input
                        onChange={handleSearch}
                        type="text"
                        className="form-control"
                        placeholder="Search tasks"
                    />
                </div>
            </div>
            {/* list of items */}
            <div className="d-flex flex-column w-100">
                <h2>Reminders</h2>
                {reminders.map(task => (
                    <div key={task._id} className="m-2 p-2 border bg-light w-100 rounded-3 d-flex justify-content-between align-item-center">
                        <div>
                            <span className={task.isDone ? 'text-decoration-line-through' : ''}>
                                {task.taskName}
                            </span>
                            <br />
                            <small>Due Date: {new Date(task.dueDate).toLocaleDateString()}</small>
                        </div>
                        <div>
                            <button onClick={() => handleCheckAndUncheck(task)} className="btn btn-success btn-sm me-2" type="button">
                                <FaCheck />
                            </button>
                            <button onClick={() => setupdateTask(task)} className="btn btn-primary btn-sm me-2" type="button">
                                <FaPencilAlt />
                            </button>
                            <button onClick={() => handleDeleteTask(task._id)} className="btn btn-danger btn-sm me-2" type="button">
                                <FaTrash />
                            </button>
                        </div>
                    </div>
                ))}
            </div>

            {/* Toastify */}
            <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />
        </div>
    );

}

export default TaskManager;
