import { API_URL } from "./utils"

export const CreateTask = async (taskObj)=>{
    const url = API_URL;
    const options = {
        method:'POST',
        headers:{
            'Content-type':'application/json'
        },
        body: JSON.stringify(taskObj)
    };
    try { 
        const result  = await fetch(url,options);
        const data = await result.json(); 
        return data;   
    } catch (err) {
        return err; 
    }
}
