import React, { useState } from "react";
import { FaCheck, FaPencilAlt, FaPlus, FaSearch,   FaTrash } from "react-icons/fa";
import { ToastContainer } from "react-toastify";
import { CreateTask } from "./api";
import { notify } from "../../Backend/Routes/TaskRouter";
function TaskManager() {
const [input , setInput] = useState('')

const handleAddtask= async ()=>{
    const obj ={
        taskName:input,
        isDone:false
    }
    try {
       const {success,message} =  await CreateTask(obj);
       if(success){
        //show success toast
        notify(message,'success')
       }else{
        // show error toast
        notify(message,'error')
       }
    } catch (err) {
        console.error(err);
        notify('Failed to create task','error')
    }
}
    return (
        <div className='d-flex flex-column align-items-center w-50 m-auto mt-5'>
            <h1 className="mb-5">Task Management System</h1>
            {/* input and search-box */}
            <div className="d-flex justify-content-between align-item-center mb-4 w-100">
                <div className="input-group flex-grow-1 me-2">
                    <input type="text"value={input} onChange={(e) => setInput(e.target.value)} className="form-control me-1" placeholder="Add a new Task" />
                    <button onClick={handleAddtask}className="btn btn-success btn-sm me-2">
                        <FaPlus className='m-2' />
                    </button>
                </div>

                <div className="input-group flex-grow-1 ">
                    <span className="input-group-text">
                        <FaSearch />
                    </span>
                    <input type="text" className="form-control" placeholder="Search tasks" />
                </div>
            </div>
            {/* list of items */}
            <div className="d-flex flex-column w-100">
                <div className="m-2 p-2 border bg-light w-100 rounded-3 d-flex justify-content-between align-item-center ">
                    <span className="text-decoration-ling-through">
                        First todo task
                    </span>
                    <div className="">
                        <button className="btn btn-success btn-sm me-2" type="button">
                            <FaCheck />
                        </button>
                        <button className="btn btn-primary btn-sm me-2" type="button">
                            <FaPencilAlt />
                        </button>
                        <button className="btn btn-danger btn-sm me-2" type="button">
                            <FaTrash />
                        </button>
                    </div>
                </div>
            </div>

            {/* Toastify */}
            <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />           
        </div>
    )
}

export default TaskManager;
