import React, { useEffect, useState } from "react";
import { FaCheck, FaPencilAlt, FaPlus, FaSearch,   FaTrash } from "react-icons/fa";
import { ToastContainer } from "react-toastify";
import { CreateTask, DeleteTask, GetAllTasks, UpdateTask } from "./api";
import { notify } from "./utils";
import './TaskManager.css'; 


function TaskManager() {
const [input , setInput] = useState('');
const [tasks, setTasks] = useState([]);
const [copyTasks,setcopyTasks] = useState([]);
const [updateTask,setupdateTask] = useState(null);

const handleTask = ()=>{
    if(updateTask && input){
//update api call
console.log('update api call');
const obj ={
    taskName:input,
    isDone: updateTask.isDone,
    _id:updateTask._id
}
handleUpdate(obj);
    }else if (updateTask === null && input){
console.log('create api call');

        handleAddtask();
    }
    setInput('');
}

useEffect(()=>{
    if (updateTask){
        setInput(updateTask.taskName);
    }
},[updateTask])
const handleAddtask= async ()=>{
    const obj ={
        taskName:input,
        isDone:false
    }
    try {
       const {success,message} =  await CreateTask(obj);
       if(success){
        //show success toast
        notify(message,'success')
       }else{
        // show error toast
        notify(message,'error')
       }
       setInput('');
       fetchAllTask();
    } catch (err) {
        console.error(err);
        notify('Failed to create task','error')
    }
}
const fetchAllTask = async()=>{
    try {
         const {data} =  await GetAllTasks();
         setTasks(data);
         setcopyTasks(data);
        }catch (err) {
                console.error(err);
                notify('Failed to fetch task','error')
        }
}
useEffect(()=>{
    fetchAllTask();
})

const handleDeleteTask = async(id) =>{
    try {
        const {success,message} =  await DeleteTask(id);
        if(success){
            //show success toast
            notify(message,'success')
           }else{
            // show error toast
            notify(message,'error')
           }
           fetchAllTask();
       }catch (err) {
               console.error(err);
               notify('Failed to delete task','error')
           }
}
const handleCheckAndUncheck = async (item) => {
    const { _id, isDone, taskName } = item;
    const obj = {
        taskName,
        isDone: !isDone
    }
    try {
        const { success, message } = await UpdateTask(_id, obj);
        if (success) {
            //show success toast
            notify(message, 'success')
        } else {
            //show error toast
            notify(message, 'error')
        }
        fetchAllTask()
    } catch (err) {
        console.error(err);
        notify('Failed to create task', 'error')
    }
}
const handleUpdate = async (item) => {
    const { _id, isDone, taskName } = item;
    const obj = {
        taskName,
        isDone: !isDone
    }
    try {
        const { success, message } = await UpdateTask(_id, obj);
        if (success) {
            //show success toast
            notify(message, 'success')
        } else {
            //show error toast
            notify(message, 'error')
        }
        fetchAllTask()
    } catch (err) {
        console.error(err);
        notify('Failed to create task', 'error')
    }
}

const handleSearch = (e)=>{
    const term = e.target.value.toLowerCase();
    const oldTask = [...copyTasks];
    const results = oldTask.filter((item)=>item.taskName.toLowerCase().includes(term));
    setTasks(results)
}


    return (
        <div className='d-flex flex-column align-items-center w-50 m-auto mt-5'>
            <h1 className="mb-5">Task Management System</h1>
            {/* input and search-box */}
            <div className="d-flex justify-content-between align-item-center mb-4 w-100">
                <div className="input-group flex-grow-1 me-2">
                    <input type="text"value={input} onChange={(e) => setInput(e.target.value)} className="form-control me-1" placeholder="Add a new Task" />
                    <button onClick={handleTask}className="btn btn-success btn-sm me-2">
                        <FaPlus className='m-2' />
                    </button>
                </div>

                <div className="input-group flex-grow-1 ">
                    <span  className="input-group-text">
                        <FaSearch />
                    </span>
                    <input onClick={handleSearch} type="text" className="form-control" placeholder="Search tasks" />
                </div>
            </div>
            {/* list of items */}
            <div className="d-flex flex-column w-100">
                {
                    tasks.map((item)=>  (
                        <div key={item.id} className="m-2 p-2 border bg-light w-100 rounded-3 d-flex justify-content-between align-item-center ">
                    <span className={item.isDone ? 'text-decoration-line-through' : ''}>
                        {item.taskName}
                    </span>
                    <div className="">
                        <button onClick={()=>handleCheckAndUncheck(item)} className="btn btn-success btn-sm me-2" type="button">
                            <FaCheck />
                        </button>
                        <button onClick={()=> setupdateTask(item)}className="btn btn-primary btn-sm me-2" type="button">
                            <FaPencilAlt />
                        </button>
                        <button onClick={()=>handleDeleteTask(item._id)} className="btn btn-danger btn-sm me-2" type="button">
                            <FaTrash />
                        </button>
                    </div>
                </div>
                    ))
                }
            </div>

            {/* Toastify */}
            <ToastContainer position="top-right" autoClose={3000} hideProgressBar={false} />           
        </div>
    )
}

export default TaskManager;
